import { Component, OnInit, signal } from '@angular/core';
import { Mascota, MascotaService, Tipo } from '../../service/mascota.service';

type ContenidoTarjeta = {
  nombre: String;
  tipo: String,
  descripcion: String;
  imagen?: String
}

@Component({
  selector: 'app-lista-mascotas',
  standalone: false,

  templateUrl: './lista-mascotas.component.html',
  styleUrl: './lista-mascotas.component.css'
})
export class ListaMascotasComponent implements OnInit {

  public mascotasEj: Mascota[] = [
    { nombre: "Boñato", tipo: Tipo.AVE, descripcion: "Canta cada mañana como si le pagaran doble por hacerte sufrir.", imagen: "https://upload.wikimedia.org/wikipedia/commons/c/c1/Red_Junglefowl_%28male%29_-_Thailand.jpg" },
    { nombre: "Ataulfo", tipo: Tipo.CANINO, descripcion: "Ignora todas las órdenes pero tampoco sabe lo que hace. Tiene la cabeza llena de aire.", imagen: "data:image/jpeg;base64," },
    { nombre: "Juan", tipo: Tipo.EQUINO, descripcion: "Juan", imagen: "data:image/jpeg;base64," },
    { nombre: "Margarito", tipo: Tipo.FELINO, descripcion: "Heredó la esquizofrenia de su dueño.", imagen: "data:image/jpeg;base64," },
    { nombre: "Polish", tipo: Tipo.RUMIANTE, descripcion: "Tylko jedno w głowie mam Koksu pięć gram odlecieć sam W krainę za zapomnienia", imagen: "data:image/jpeg;base64," },
    { nombre: "Leonardo", tipo: Tipo.REPTIL, descripcion: "Tan frío como tu ex pero al menos te mira a la cara.", imagen: "data:image/jpeg;base64," },
    { nombre: "Fat Tony", tipo: Tipo.ROEDOR, descripcion: "La envidia es una enfermedad. Espero que te cures pronto.", imagen: "data:image/jpeg;base64," },
    { nombre: "Tom", tipo: Tipo.PEZ, descripcion: "CHOCOLATEEEEEEEEEEEEEE", imagen: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTRsouBU9KRKnXL6hfyyERr6GW2t4kICrMWrA&s" }
  ];
  tarjetas = signal<ContenidoTarjeta[]>([]);


  constructor(private mascotaService: MascotaService) {

  }
  ngOnInit() {
    this.mascotaService.verTodasMascotas().subscribe((mascotas) => {
      const tarjetas: ContenidoTarjeta[] = mascotas.map(mascota => ({
        nombre: mascota.nombre,
        tipo: mascota.tipo,
        descripcion: mascota.descripcion,
        imagen: mascota.imagen
      }));

      this.tarjetas.set(tarjetas);
    })
  }

  verPorTipo(tipo : Tipo){
    this.mascotaService.verTodasMascotasPorTipo(tipo).subscribe((mascotas)=>{
      const tarjetas: ContenidoTarjeta[] = mascotas.map(mascota => ({
        nombre: mascota.nombre,
        tipo: mascota.tipo,
        descripcion: mascota.descripcion,
        imagen: mascota.imagen
      }));

      this.tarjetas.set(tarjetas);
    })
  }
}
